// src/components/Hero.js
import React, { useEffect, useState } from "react";
import img1 from "../img/img1.jpg"; // your hero image

function Hero() {
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    // Trigger slide-in animation once the component mounts
    setAnimate(true);
  }, []);

  return (
    <section
      className="hero"
      id="home"
      style={{
        backgroundColor: "#000", // black background for contrast
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "flex-start", // left align the wedge
        overflow: "hidden"
      }}
    >
      {/* SVG with clipPath that creates a large quarter-circle wedge */}
      <svg
        width="1000"
        height="1000"
        viewBox="0 0 1000 1000"
        style={{
          transform: animate ? "translateX(0)" : "translateX(-100%)",
          opacity: animate ? 1 : 0,
          transition: "transform 1s ease-out, opacity 1s ease-out"
        }}
      >
        <defs>
          <clipPath id="quarterCircleClip">
            {/*
              Three points:
                1) Move to (0,500): This is the circle's center (placed at the left edge, vertically centered).
                2) Line to (500,500): Right-most point (middle of the circle horizontally).
                3) Arc from (500,500) to (0,1000) with radius 500, forming the curved edge.
                4) Z: Closes the path back to (0,500).

              This gives us a bottom-right quarter circle wedge (a pizza slice shape) that is now significantly larger.
            */}
            <path d="M 0,900 L 900,900 A 900,900 0 0 1 0,1000 Z" />
          </clipPath>
        </defs>

        <image
          href={img1}
          clipPath="url(#quarterCircleClip)"
          x="0"
          y="0"
          width="1000"
          height="1000"
          preserveAspectRatio="xMidYMid meet"
        />
      </svg>

      {/* Hero text slides in from the right */}
      <div
        style={{
          marginLeft: "2rem",
          color: "#fff",
          transform: animate ? "translateX(0)" : "translateX(100%)",
          opacity: animate ? 1 : 0,
          transition: "transform 1s ease-out, opacity 1s ease-out"
        }}
      >
        <h1 style={{ fontSize: "3rem", margin: 0 }}>
          Professional Nursing Recruiting Agency (PNRA)
        </h1>
        <p style={{ fontSize: "1.5rem", margin: "0.5rem 0 0" }}>
          Bridging the gap in healthcare needs
        </p>
      </div>
    </section>
  );
}

export default Hero;
