// src/components/Navbar.js
import React, { useState } from "react";
import "../App.css";

function Navbar() {
  // We'll use a Boolean for the language state:
  // true = English, false = Amharic.
  const [isEnglish, setIsEnglish] = useState(true);

  const handleToggle = () => {
    setIsEnglish(!isEnglish);
  };

  return (
    <nav className="navbar">
      <h1 className="navbar-title">PNRA</h1>
      <ul className="navbar-links">
        <li>
          <a href="#home">{isEnglish ? "Home" : "መነሻ"}</a>
        </li>
        <li>
          <a href="#about">{isEnglish ? "About Us" : "ስለ እኛ"}</a>
        </li>
        <li>
          <a href="#mission">{isEnglish ? "Mission" : "ተልዕኮ"}</a>
        </li>
        <li>
          <a href="#approach">{isEnglish ? "Our Approach" : "የእኛ ዘዴ"}</a>
        </li>
      </ul>
      {/* Language toggle */}
      <div className="language-toggle" onClick={handleToggle}>
        <div className={`toggle-slider ${isEnglish ? "english" : "amharic"}`}>
          {isEnglish ? "EN" : "አማ"}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
