// src/components/Hero.js
import React, { useEffect, useState } from "react";
import img1 from "../img/img1.jpg"; // your hero image

function Hero() {
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true);
  }, []);

  return (
    <section 
      className="hero" 
      id="home" 
      style={{
        backgroundColor: "#000",     // black background for contrast
        height: "100vh", 
        display: "flex", 
        alignItems: "center", 
        justifyContent: "center",
        overflow: "hidden"           // hide any overflow
      }}
    >
      {/* Large SVG that will clip to the bottom-right quarter of a circle */}
      <svg
        width="600"
        height="600"
        viewBox="0 0 600 600"
        style={{
          transform: animate ? "translateX(0)" : "translateX(-100%)",
          opacity: animate ? 1 : 0,
          transition: "transform 1s ease-out, opacity 1s ease-out"
        }}
      >
        {/* Define a clipPath for a bottom-right quarter-circle wedge */}
        <defs>
          <clipPath id="quarterClip">
            {/*
              Path explanation:
              - M300,300 moves “pen” to the center (of 600x600)
              - L600,300 draws a line to the right-middle
              - A300,300 0 0 1 300,600 draws a 90° arc to bottom-middle
              - Z closes the path back to (300,300)
              This yields the bottom-right 1/4 slice of the circle.
            */}
            <path d="M300,300 L600,300 A300,00 0 0 1 300,600 Z" />
          </clipPath>
        </defs>

        {/* The image is placed inside the SVG and clipped to the wedge shape */}
        <image
          href={img1}
          clipPath="url(#quarterClip)"   // Use our quarter-circle clipPath
          x="0"
          y="0"
          width="600"
          height="600"
          preserveAspectRatio="xMidYMid meet"
        />
      </svg>

      {/* Hero text sliding in from the right */}
      <div 
        style={{
          marginLeft: "2rem",
          color: "#fff",
          transform: animate ? "translateX(0)" : "translateX(100%)",
          opacity: animate ? 1 : 0,
          transition: "transform 1s ease-out, opacity 1s ease-out"
        }}
      >
        <h1 style={{ fontSize: "3rem", margin: 0 }}>
          Professional Nursing Recruiting Agency (PNRA)
        </h1>
        <p style={{ fontSize: "1.5rem", margin: "0.5rem 0 0" }}>
          Bridging the gap in healthcare needs
        </p>
      </div>
    </section>
  );
}

export default Hero;
