// src/components/About.js
import React from "react";
import './About.css';
import useInView from "../useInView.js";

function About() {
  const [ref, inView] = useInView({ threshold: 0.2 });

  return (
    <section id="about" className={`about ${inView ? 'animate' : ''}`} ref={ref}>
      <h2>About Us</h2>
      <p>
        Professional Nursing Recruiting Agency (PNRA) is a U.S-based nonprofit organization dedicated 
        to bridging healthcare gaps by recruiting and supporting qualified international nurses for 
        placement in American healthcare facilities.
      </p>
    </section>
  );
}

export default About;
