// src/components/Navbar.js
import React, { useContext } from "react";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate for programmatic navigation
import LanguageContext from "../LanguageContext.js";
import "./Navbar.css";

function Navbar() {
  const { isEnglish, toggleLanguage } = useContext(LanguageContext);
  const navigate = useNavigate(); // React Router's navigation hook

  const navText = {
    home: isEnglish ? "Home" : "መነሻ",
    about: isEnglish ? "About Us" : "ስለ እኛ",
    mission: isEnglish ? "Mission" : "ተልዕኮ",
    approach: isEnglish ? "Our Approach" : "የእኛ ዘዴ",
    fillForm: isEnglish ? "Fill Out Form" : "ቅጽ ይሙሉ",
  };

  // Handle hash links by navigating to "/" and appending the hash
  const handleHashNavigation = (hash) => {
    navigate(`/#${hash}`);
  };

  return (
    <nav className="navbar">
      <h1 className="navbar-title">PNRA</h1>
      <ul className="navbar-links">
        <li><a onClick={() => handleHashNavigation("home")}>{navText.home}</a></li>
        <li><Link to="/about-us">{navText.about}</Link></li>
        <li><a onClick={() => handleHashNavigation("mission")}>{navText.mission}</a></li>
        <li><a onClick={() => handleHashNavigation("approach")}>{navText.approach}</a></li>
        <li>
          <a href="https://forms.gle/AAuDcCMY5bfE3xiE9" target="_blank" rel="noopener noreferrer">
            {navText.fillForm}
          </a>
        </li>
      </ul>
      <div className="language-toggle" onClick={toggleLanguage}>
        <div className={`toggle-slider ${isEnglish ? "english" : "amharic"}`}>
          {isEnglish ? "EN" : "አማ"}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
