// src/components/Navbar.js
import React, { useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom"; // Import React Router hooks
import LanguageContext from "../LanguageContext.js";
import "./Navbar.css";

function Navbar() {
  const { isEnglish, toggleLanguage } = useContext(LanguageContext);
  const navigate = useNavigate(); // Navigation hook
  const location = useLocation(); // Get current location

  const navText = {
    home: isEnglish ? "Home" : "መነሻ",
    about: isEnglish ? "About Us" : "ስለ እኛ",
    mission: isEnglish ? "Mission" : "ተልዕኮ",
    approach: isEnglish ? "Our Approach" : "የእኛ ዘዴ",
    fillForm: isEnglish ? "Fill Out Form" : "ቅጽ ይሙሉ",
  };

  // Handle navigation to sections
  const handleNavigation = (sectionId) => {
    if (location.pathname === "/") {
      // On the home page, scroll to the section
      const section = document.getElementById(sectionId);
      if (section) {
        section.scrollIntoView({ behavior: "smooth" });
      }
    } else {
      // If not on the home page, navigate to the home page first
      navigate("/");
      // Scroll to the section after navigating back to the home page
      setTimeout(() => {
        const section = document.getElementById(sectionId);
        if (section) {
          section.scrollIntoView({ behavior: "smooth" });
        }
      }, 100); // Add a short delay to ensure the home page has loaded
    }
  };

  return (
    <nav className="navbar">
      <h1 className="navbar-title">PNRA</h1>
      <ul className="navbar-links">
        <li onClick={() => handleNavigation("home")}>{navText.home}</li>
        <li>
          <a href="/about-us">{navText.about}</a>
        </li>
        <li onClick={() => handleNavigation("mission")}>{navText.mission}</li>
        <li onClick={() => handleNavigation("approach")}>{navText.approach}</li>
        <li>
          <a href="https://forms.gle/AAuDcCMY5bfE3xiE9" target="_blank" rel="noopener noreferrer">
            {navText.fillForm}
          </a>
        </li>
      </ul>
      <div className="language-toggle" onClick={toggleLanguage}>
        <div className={`toggle-slider ${isEnglish ? "english" : "amharic"}`}>
          {isEnglish ? "EN" : "አማ"}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
