// src/components/Navbar.js
import React, { useContext } from "react";
import { Link } from "react-router-dom";
import LanguageContext from "../LanguageContext.js";
import "../App.css";

function Navbar() {
  const { isEnglish, toggleLanguage } = useContext(LanguageContext);

  // Navigation text based on language selection.
  const navText = {
    home: isEnglish ? "Home" : "መነሻ",
    about: isEnglish ? "About Us" : "ስለ እኛ",
    mission: isEnglish ? "Mission" : "ተልዕኮ",
    approach: isEnglish ? "Our Approach" : "የእኛ ዘዴ",
    fillForm: isEnglish ? "Fill Out Form" : "ቅጽ ይሙሉ",
  };

  return (
    <nav className="navbar">
      <h1 className="navbar-title">PNRA</h1>
      <ul className="navbar-links">
        <li>
          <a href="#home">{navText.home}</a>
        </li>
        <li>
          <a href="#about">{navText.about}</a>
        </li>
        <li>
          <a href="#mission">{navText.mission}</a>
        </li>
        <li>
          <a href="#approach">{navText.approach}</a>
        </li>
        <li>
          {/* Link that opens the external form in a new tab */}
          <a
            href="https://forms.gle/AAuDcCMY5bfE3xiE9"
            target="_blank"
            rel="noopener noreferrer"
          >
            {navText.fillForm}
          </a>
        </li>
      </ul>
      {/* Language toggle */}
      <div className="language-toggle" onClick={toggleLanguage}>
        <div className={`toggle-slider ${isEnglish ? "english" : "amharic"}`}>
          {isEnglish ? "EN" : "አማ"}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
