import React, { useState } from "react";
import Navbar from "./components/Navbar.js"; 
import Home from "./components/Home.js"; 
import About from "./components/About.js"; 
import Mission from "./components/Mission.js"; 
import Approach from "./components/Approach.js"; 
import Footer from "./components/Footer.js"; 
import AuthPage from "./components/AuthPage.js";  {/* **Added .js extension to the import** */}
import { auth, db } from './firebase.js'; // Import Firebase functions
import { createUserWithEmailAndPassword } from 'firebase/auth'; // For user sign-up
import { addDoc, collection } from 'firebase/firestore'; // For adding data to Firestore
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';  // **New Imports for Routing**
import "./App.css";

function App() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Handle user sign-up (Example)
  const handleSignUp = async (e) => {
    e.preventDefault();
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      console.log("User signed up successfully!");
    } catch (error) {
      console.error("Error signing up:", error);
    }
  };

  // Handle form submission (Example)
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, "users"), {
        email,
        // Add other data you want to store
      });
      console.log("Data submitted to Firestore!");
    } catch (error) {
      console.error("Error submitting data:", error);
    }
  };

  return (
    <Router>  {/* **Wrap your app in Router to handle routes** */}
      <Navbar />
      <Routes>  {/* **Define the routes** */}
        <Route path="/" element={<Home />} />
        <Route path="/auth" element={<AuthPage />} />  {/* **New route for AuthPage** */}
        <Route path="/login" element={<AuthPage />} />  {/* **New route for Login page** */}
        <Route path="/signup" element={<AuthPage />} />  {/* **New route for Signup page** */}
        <Route path="/about" element={<About />} />
        <Route path="/mission" element={<Mission />} />
        <Route path="/approach" element={<Approach />} />
      </Routes>

      {/* Example of a form (for sign-up) */}
      <form onSubmit={handleSignUp}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Sign Up</button>
      </form>

      {/* Form to submit data */}
      <form onSubmit={handleFormSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {/* Other fields */}
        <button type="submit">Submit Data</button>
      </form>

      <Footer />
    </Router>);
}

export default App;
