// src/components/Approach.js
import React, { useContext } from "react";
import LanguageContext from "../LanguageContext.js";
import "../App.css";

function Approach() {
  const { isEnglish } = useContext(LanguageContext);

  // Text for each approach item
  const approachItems = [
    {
      title: isEnglish ? "Strategic Partnership" : "የስራ ስርአተ እኩልነት",
      description: isEnglish
        ? "Collaborate with healthcare institutions, training centers, and recruitment agencies worldwide."
        : "ከዓለም ላይ ያሉ ጤና ተቋማት፣ ስልጠና ማዕከላት እና ሪክሩትመንት ድርጅቶች ጋር በመስራት የሚደርሰው።",
    },
    {
      title: isEnglish ? "Step-by-Step Support" : "የደረጃ ደረጃ ድጋፍ",
      description: isEnglish
        ? "Guide candidates through every phase of U.S. licensure and employment."
        : "እንደ ሙሉ ደረጃ ለአሜሪካ ፈቃድ እና ስራ ሂደት እንደገና ተሟላ ደረጃ ለመመሪያ።",
    },
    {
      title: isEnglish ? "Trusted Support" : "የታማኝ ድጋፍ",
      description: isEnglish
        ? "We provide a clear path through complex immigration and licensure processes."
        : "በውስጣዊ እና ውጭ ሂደቶች ውስጥ ግልጽ መንገድ እንዲኖረው ድጋፍ እንሰጣለን።",
    },
    {
      title: isEnglish ? "Targeted Marketing & Outreach" : "የተመረጡ ማርኬቲንግ እና መደራረት",
      description: isEnglish
        ? "Use digital marketing, webinars, and online job portals to connect with potential candidates globally."
        : "በዲጂታል ማርኬቲንግ ፣ ዌብናር፣ እና የመስመር ስራ ቦታዎች ተያያዥ ተሟላ ተጠቃሚ እንዲቀጥሉ የማስያዣ መንገድ እንደሆን።",
    },
    {
      title: isEnglish ? "Expert Guidance" : "የሙያ ምክር",
      description: isEnglish
        ? "Our dedicated team assists you at every stage, from document review to placement."
        : "አስተዳደር ቡድናችን ከሰነዶች እስከ መቀመጫ በሚያስፈልገው የሁሉም ደረጃ ድጋፍ ይሰጣሉ።",
    },
    {
      title: isEnglish ? "Smooth Transition" : "ቀላል ለውጥ",
      description: isEnglish
        ? "Resources for cultural integration and training to help you thrive in the U.S. healthcare environment."
        : "ለሙሉ ሁኔታ ቅርብ ባህላዊ መገናኘት እና ስልጠና ምንጮች እንዲሆኑ ድጋፍ ይሰጣሉ።",
    },
  ];

  return (
    <section id="approach" className="approach">
      <h2>{isEnglish ? "Our Approach" : "የእኛ ዘዴ"}</h2>
      {approachItems.map((item, idx) => (
        <div className="approach-item" key={idx}>
          <h3>{item.title}</h3>
          <p>{item.description}</p>
        </div>
      ))}
    </section>
  );
}

export default Approach;
