// src/components/Hero.js
import React, { useEffect, useState } from "react";
import img1 from "../img/img1.jpg"; // Import the image file
import "./Hero.css";

function Hero() {
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true);
  }, []);

  return (
    <section className="hero" id="home">
      <div className={`hero-image ${animate ? "slide-in" : ""}`}>
        <img src={img1} alt="Hero" />
      </div>
      <div className={`hero-text ${animate ? "slide-in-right" : ""}`}>
        <h1>Professional Nursing Recruiting Agency (PNRA)</h1>
        <p>Bridging the gap in healthcare needs</p>
      </div>
    </section>
  );
}

export default Hero;
