import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";  // Import Link for routing
import { auth } from "../firebase.js";  // Firebase auth
import { signOut } from "firebase/auth";  // Firebase sign-out function
import "../App.css";

function Navbar() {
  const [user, setUser] = useState(null);

  // Monitor auth state
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        setUser(user);  // User is logged in
      } else {
        setUser(null);  // No user is logged in
      }
    });

    return unsubscribe;  // Clean up listener on component unmount
  }, []);

  const handleSignOut = () => {
    signOut(auth)  // Sign out the user
      .then(() => {
        console.log("User signed out");
      })
      .catch((error) => {
        console.error("Error signing out:", error);
      });
  };

  return (
    <nav className="navbar">
      <h1 className="navbar-title">PNRA</h1>
      <ul className="navbar-links">
        <li><Link to="/">Home</Link></li>  {/* Change from anchor tag to Link */}
        <li><Link to="/about">About Us</Link></li>  {/* Change from anchor tag to Link */}
        <li><Link to="/mission">Mission</Link></li>  {/* Change from anchor tag to Link */}
        <li><Link to="/approach">Our Approach</Link></li>  {/* Change from anchor tag to Link */}

        {/* Conditionally render Login/Sign Up button or Sign Out button */}
        {user ? (
          <li>
            <button className="auth-button" onClick={handleSignOut}>Sign Out</button>
          </li>
        ) : (
          <li>
            <Link to="/auth">
              <button className="auth-button">Login / Sign Up</button>
            </Link>
          </li>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
