// src/components/Hero.js
import React, { useEffect, useState } from "react";
import img1 from "../img/img1.jpg"; // your hero image

function Hero() {
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    // Trigger slide-in animation once the component mounts
    setAnimate(true);
  }, []);

  return (
    <section
      className="hero"
      id="home"
      style={{
        backgroundColor: "#000", // black background for contrast
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        overflow: "hidden"
      }}
    >
      {/* SVG that clips the image to a bottom-right quarter-circle */}
      <svg
        width="600"
        height="600"
        viewBox="0 0 600 600"
        style={{
          transform: animate ? "translateX(0)" : "translateX(-100%)",
          opacity: animate ? 1 : 0,
          transition: "transform 1s ease-out, opacity 1s ease-out"
        }}
      >
        <defs>
          <clipPath id="quarterCircleClip">
            {/*
              We have exactly three points in this path:

              1) Move to (300, 300) --> the circle center
              2) Line to (600, 300) --> right-middle
              3) Arc down to (300, 600) --> bottom-middle
              Then Z closes back to the first point (300,300), 
              but does NOT add a new visible corner.
            */}
            <path d="M 300,300 
                     L 600,300
                     A 300,300 1 0 1 300,700
                     Z" />
          </clipPath>
        </defs>

        <image
          href={img1}
          clipPath="url(#quarterCircleClip)"
          x="0"
          y="0"
          width="600"
          height="600"
          preserveAspectRatio="xMidYMid meet"
        />
      </svg>

      {/* Hero text slides in from the right */}
      <div
        style={{
          marginLeft: "2rem",
          color: "#fff",
          transform: animate ? "translateX(0)" : "translateX(100%)",
          opacity: animate ? 1 : 0,
          transition: "transform 1s ease-out, opacity 1s ease-out"
        }}
      >
        <h1 style={{ fontSize: "3rem", margin: 0 }}>
          Professional Nursing Recruiting Agency (PNRA)
        </h1>
        <p style={{ fontSize: "1.5rem", margin: "0.5rem 0 0" }}>
          Bridging the gap in healthcare needs
        </p>
      </div>
    </section>
  );
}

export default Hero;
