// src/components/AuthPage.js
import React, { useState } from "react";
import { auth } from "../firebase.js"; // Import Firebase auth
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth"; // Import for sign-up and login

function AuthPage() {
  const [isSignUp, setIsSignUp] = useState(true); // Track mode
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isSignUp) {
        await createUserWithEmailAndPassword(auth, email, password);
        console.log("User signed up successfully!");
      } else {
        await signInWithEmailAndPassword(auth, email, password);
        console.log("User logged in successfully!");
      }
    } catch (error) {
      console.error("Error:", error.message);
    }
  };

  return (
    <div className="auth-container">
      <h2>{isSignUp ? "Sign Up" : "Log In"}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">{isSignUp ? "Sign Up" : "Log In"}</button>
      </form>
      <button onClick={() => setIsSignUp(!isSignUp)}>
        {isSignUp ? "Already have an account? Log In" : "Don't have an account? Sign Up"}
      </button>
    </div>
  );
}

export default AuthPage;
